// 電池配置 - 最終修正版
const batteries = {
    makita: {
        entityId: 'switch.tp_link_power_strip_eb2f_cha_shang_4',
        name: '牧田BL1041B充電電池',
        chargingTime: 90,
        maintenanceDays: 30,
        domain: 'switch'
    },
    eneloop_aa: {
        entityId: 'switch.tp_link_power_strip_eb2f_cha_shang_5',
        name: 'ENELOOP 3號充電電池',
        chargingTime: 240,
        maintenanceDays: 60,
        domain: 'switch'
    },
    eneloop_aaa: {
        entityId: 'switch.tp_link_power_strip_eb2f_cha_shang_6',
        name: 'ENELOOP 4號充電電池',
        chargingTime: 210,
        maintenanceDays: 60,
        domain: 'switch'
    }
};

// 通知服務配置
const notifications = {
    services: ['notify.notify', 'notify.synology_chat_bot_3', 'notify.telegram'],
    enabled: true,
    quietHours: { start: 22, end: 7 }
};

// 儲存配置
flow.set('batteries', batteries);
flow.set('notifications', notifications);

// 初始化充電記錄
const records = flow.get('chargingRecords') || {};
Object.keys(batteries).forEach(key => {
    if (!records[key]) {
        records[key] = {
            lastCharge: null,
            count: 0
        };
    }
});
flow.set('chargingRecords', records);

msg.payload = {
    status: 'initialized',
    batteries: Object.keys(batteries).length,
    notifications: notifications.services.length,
    timestamp: new Date().toISOString(),
    config: batteries
};

node.status({fill:"green", shape:"dot", text:"系統已初始化"});

return msg;

// 獲取配置
const batteries = flow.get('batteries');
if (!batteries) {
    node.error('系統未初始化，請先執行系統初始化');
    return null;
}

// 解析狀態變化
const newState = msg.payload;
const oldState = msg.data.old_state?.state;
const entityId = msg.data.entity_id;

// 找到對應的電池
let batteryKey = null;
let battery = null;

for (const [key, config] of Object.entries(batteries)) {
    if (config.entityId === entityId) {
        batteryKey = key;
        battery = config;
        break;
    }
}

if (!battery) {
    node.error(`找不到實體 ${entityId} 的配置`);
    return null;
}

// 準備輸出
const outputs = [null, null, null]; // [通知, 控制, debug]

// 檢查開啟狀態 (off -> on)
if (oldState === 'off' && newState === 'on') {
    // 清除現有計時器
    const timerId = flow.get(`${batteryKey}_timer`);
    if (timerId) {
        clearTimeout(timerId);
    }

    // 記錄開始時間
    const startTime = new Date();
    flow.set(`${batteryKey}_start`, startTime.getTime());

    // 更新充電記錄
    const records = flow.get('chargingRecords') || {};
    if (!records[batteryKey]) records[batteryKey] = {};
    records[batteryKey].lastCharge = startTime.toISOString();
    records[batteryKey].count = (records[batteryKey].count || 0) + 1;
    flow.set('chargingRecords', records);

    // 設定自動關閉計時器
    const chargingMs = battery.chargingTime * 60 * 1000;
    const newTimerId = setTimeout(() => {
        // 發送關閉命令
        const controlMsg = {
            payload: {
                domain: battery.domain,
                service: 'turn_off',
                target: {
                    entity_id: entityId
                }
            },
            batteryKey: batteryKey,
            action: 'auto_stop'
        };

        // 發送到控制輸出
        node.send([null, controlMsg, {
            payload: `自動關閉 ${battery.name}`,
            batteryKey: batteryKey,
            action: 'auto_stop',
            controlMsg: controlMsg
        }]);

        flow.set(`${batteryKey}_timer`, null);
    }, chargingMs);

    flow.set(`${batteryKey}_timer`, newTimerId);

    // 準備通知訊息
    const notifyMsg = {
        payload: {
            title: '🔋 充電開始',
            message: `🔋 ${battery.name} 開始充電\n⏱️ 預計時間：${battery.chargingTime}分鐘\n🕐 開始時間：${startTime.toLocaleString('zh-TW')}`
        },
        batteryKey: batteryKey,
        action: 'start'
    };

    outputs[0] = notifyMsg;
    outputs[2] = {
        payload: `${battery.name} 開始充電`,
        batteryKey: batteryKey,
        chargingTime: battery.chargingTime,
        timerId: newTimerId,
        entityId: entityId,
        domain: battery.domain
    };
}

// 檢查關閉狀態 (on -> off)
else if (newState === 'off' && oldState === 'on') {
    // 清除計時器
    const timerId = flow.get(`${batteryKey}_timer`);
    if (timerId) {
        clearTimeout(timerId);
        flow.set(`${batteryKey}_timer`, null);
    }

    // 計算實際時間
    const startTime = flow.get(`${batteryKey}_start`);
    const actualTime = startTime ? Math.round((Date.now() - startTime) / 60000) : 0;

    // 準備完成通知
    const notifyMsg = {
        payload: {
            title: '✅ 充電完成',
            message: `✅ ${battery.name} 充電完成\n⏱️ 充電時長：${actualTime}分鐘\n🕐 完成時間：${new Date().toLocaleString('zh-TW')}`
        },
        batteryKey: batteryKey,
        action: 'complete'
    };

    outputs[0] = notifyMsg;
    outputs[2] = {
        payload: `${battery.name} 充電完成`,
        batteryKey: batteryKey,
        actualTime: actualTime,
        entityId: entityId
    };
}

return outputs;

// 檢查是否有通知訊息
if (!msg || !msg.payload) {
    return [null, null, null];
}

// 動態靜音時間控制
let quietMode = flow.get('quietMode');
if (quietMode === undefined) {
    quietMode = false; // 預設關閉靜音（測試模式）
    flow.set('quietMode', quietMode);
}

let isQuiet = false;
if (quietMode) {
    // 靜音模式開啟時，檢查時間
    const now = new Date();
    const hour = now.getHours();
    isQuiet = hour >= 22 || hour < 7;
}
// 如果靜音模式關閉，isQuiet 保持 false

if (isQuiet && msg.action !== 'error') {
    return [null, null, null];
}

const title = msg.payload.title;
const message = msg.payload.message;

// 創建三個通知訊息 - 修正版
const notify1 = {
    payload: {
        title: title,
        message: message
    }
};

const notify2 = {
    payload: {
        title: title,
        message: message
    }
};

const notify3 = {
    payload: {
        title: title,
        message: message
    }
};

return [notify1, notify2, notify3];

// 獲取配置
const batteries = flow.get('batteries');
const records = flow.get('chargingRecords') || {};

if (!batteries) {
    node.error('系統未初始化');
    return null;
}

const now = new Date();
const maintenanceNeeded = [];

// 檢查每個電池的維護需求
for (const [key, battery] of Object.entries(batteries)) {
    const record = records[key];

    if (!record || !record.lastCharge) {
        // 從未充電，需要維護
        maintenanceNeeded.push({
            key: key,
            battery: battery,
            reason: '從未充電',
            daysSince: '未知'
        });
        continue;
    }

    const lastCharge = new Date(record.lastCharge);
    const daysSince = Math.floor((now - lastCharge) / (1000 * 60 * 60 * 24));

    if (daysSince >= battery.maintenanceDays) {
        maintenanceNeeded.push({
            key: key,
            battery: battery,
            reason: `超過${battery.maintenanceDays}天未充電`,
            daysSince: daysSince
        });
    }
}

// 準備輸出
const outputs = [null, null, null]; // [通知, 控制, debug]

if (maintenanceNeeded.length > 0) {
    // 準備維護通知
    const batteryList = maintenanceNeeded.map(item => 
        `• ${item.battery.name} (${item.daysSince}天前)`
    ).join('\n');

    const notifyMsg = {
        payload: {
            title: '🔧 電池維護提醒',
            message: `🔧 以下電池需要維護充電：\n${batteryList}\n\n建議進行維護充電以保持電池性能。`
        },
        action: 'maintenance_alert',
        maintenanceList: maintenanceNeeded
    };

    outputs[0] = notifyMsg;

    // 自動開啟第一個需要維護的電池
    const firstBattery = maintenanceNeeded[0];
    const controlMsg = {
        payload: {
            domain: firstBattery.battery.domain,
            service: 'turn_on',
            target: {
                entity_id: firstBattery.battery.entityId
            }
        },
        batteryKey: firstBattery.key,
        action: 'maintenance_start'
    };

    outputs[1] = controlMsg;
} else {
    // 無需維護
    const notifyMsg = {
        payload: {
            title: '✅ 電池狀態良好',
            message: '✅ 所有電池狀態良好，無需維護充電。'
        },
        action: 'maintenance_ok'
    };

    outputs[0] = notifyMsg;
}

outputs[2] = {
    payload: `維護檢查完成`,
    maintenanceNeeded: maintenanceNeeded,
    totalBatteries: Object.keys(batteries).length,
    checkTime: now.toISOString()
};

return outputs;

// 獲取配置和記錄
const batteries = flow.get('batteries');
const records = flow.get('chargingRecords') || {};

if (!batteries) {
    node.error('系統未初始化');
    return null;
}

const now = new Date();
let statusReport = '📊 電池充電系統狀態報告\n\n';

// 系統資訊
statusReport += `🕐 報告時間：${now.toLocaleString('zh-TW')}\n`;
statusReport += `🔋 管理電池數量：${Object.keys(batteries).length}\n\n`;

// 各電池狀態
for (const [key, battery] of Object.entries(batteries)) {
    const record = records[key] || {};

    statusReport += `🔋 ${battery.name}\n`;
    statusReport += `   📍 插座：${battery.entityId}\n`;
    statusReport += `   ⏱️ 充電時間：${battery.chargingTime}分鐘\n`;
    statusReport += `   🔧 維護週期：${battery.maintenanceDays}天\n`;

    if (record.lastCharge) {
        const lastCharge = new Date(record.lastCharge);
        const daysSince = Math.floor((now - lastCharge) / (1000 * 60 * 60 * 24));
        statusReport += `   📅 上次充電：${lastCharge.toLocaleString('zh-TW')} (${daysSince}天前)\n`;
        statusReport += `   📈 充電次數：${record.count || 0}次\n`;

        // 維護狀態
        if (daysSince >= battery.maintenanceDays) {
            statusReport += `   ⚠️ 狀態：需要維護充電\n`;
        } else {
            statusReport += `   ✅ 狀態：良好\n`;
        }
    } else {
        statusReport += `   📅 上次充電：從未充電\n`;
        statusReport += `   📈 充電次數：0次\n`;
        statusReport += `   ⚠️ 狀態：需要初次充電\n`;
    }

    statusReport += '\n';
}

// 準備通知訊息
const notifyMsg = {
    payload: {
        title: '📊 系統狀態報告',
        message: statusReport
    },
    action: 'status_report'
};

const debugMsg = {
    payload: '狀態報告已生成',
    batteries: batteries,
    records: records,
    reportTime: now.toISOString()
};

return [notifyMsg, null, debugMsg];

// 獲取當前靜音模式狀態
let quietMode = flow.get('quietMode');
if (quietMode === undefined) {
    quietMode = false; // 預設關閉靜音（測試模式）
}

// 切換靜音模式
quietMode = !quietMode;
flow.set('quietMode', quietMode);

// 準備狀態訊息
const statusMsg = {
    payload: {
        title: quietMode ? '🔕 靜音模式已開啟' : '🔔 靜音模式已關閉',
        message: quietMode ? 
            '🔕 靜音模式已開啟\n⏰ 22:00-07:00 期間不會發送通知' : 
            '🔔 靜音模式已關閉\n📱 所有時間都會發送通知（測試模式）'
    },
    action: 'quiet_mode_change'
};

const debugMsg = {
    payload: `靜音模式: ${quietMode ? '開啟' : '關閉'}`,
    quietMode: quietMode,
    timestamp: new Date().toISOString()
};

node.status({
    fill: quietMode ? "yellow" : "green",
    shape: "dot",
    text: quietMode ? "靜音模式" : "正常模式"
});

return [statusMsg, debugMsg];
