// 電池配置 - 修正版
const batteries = {
    makita: {
        entityId: 'switch.tp_link_power_strip_eb2f_cha_shang_4',
        name: '牧田BL1041B充電電池',
        chargingTime: 90,
        maintenanceDays: 30,
        domain: 'switch'  // 修正：改為switch
    },
    eneloop_aa: {
        entityId: 'switch.tp_link_power_strip_eb2f_cha_shang_5',
        name: 'ENELOOP 3號充電電池',
        chargingTime: 240,
        maintenanceDays: 60,
        domain: 'switch'
    },
    eneloop_aaa: {
        entityId: 'switch.tp_link_power_strip_eb2f_cha_shang_6',
        name: 'ENELOOP 4號充電電池',
        chargingTime: 210,
        maintenanceDays: 60,
        domain: 'switch'
    }
};

// 通知服務配置
const notifications = {
    notifyHelper: {
        service: 'notify',
        enabled: true
    },
    synologyChat: {
        service: 'synology_chat_bot_3',
        enabled: true
    },
    telegram: {
        service: 'telegram',
        enabled: true
    }
};

// 儲存配置
flow.set('batteries', batteries);
flow.set('notifications', notifications);

// 初始化充電記錄
const records = flow.get('chargingRecords') || {};
Object.keys(batteries).forEach(key => {
    if (!records[key]) {
        records[key] = {
            lastCharge: null,
            count: 0
        };
    }
});
flow.set('chargingRecords', records);

msg.payload = {
    status: 'initialized',
    batteries: Object.keys(batteries).length,
    notifications: Object.keys(notifications).length,
    timestamp: new Date().toISOString(),
    config: batteries
};

node.status({fill:"green", shape:"dot", text:"系統已初始化"});

return msg;

// 獲取配置
const batteries = flow.get('batteries');
if (!batteries) {
    node.error('系統未初始化，請先執行系統初始化');
    return null;
}

// 解析狀態變化
const newState = msg.payload;
const oldState = msg.data.old_state?.state;
const entityId = msg.data.entity_id;

// 找到對應的電池
let batteryKey = null;
let battery = null;

for (const [key, config] of Object.entries(batteries)) {
    if (config.entityId === entityId) {
        batteryKey = key;
        battery = config;
        break;
    }
}

if (!battery) {
    node.error(`找不到實體 ${entityId} 的配置`);
    return null;
}

// 準備輸出
const outputs = [null, null, null]; // [通知, 控制, debug]

// 檢查開啟狀態 (off -> on)
if (oldState === 'off' && newState === 'on') {
    // 清除現有計時器
    const timerId = flow.get(`${batteryKey}_timer`);
    if (timerId) {
        clearTimeout(timerId);
    }

    // 記錄開始時間
    const startTime = new Date();
    flow.set(`${batteryKey}_start`, startTime.getTime());

    // 更新充電記錄
    const records = flow.get('chargingRecords') || {};
    if (!records[batteryKey]) records[batteryKey] = {};
    records[batteryKey].lastCharge = startTime.toISOString();
    records[batteryKey].count = (records[batteryKey].count || 0) + 1;
    flow.set('chargingRecords', records);

    // 設定自動關閉計時器
    const chargingMs = battery.chargingTime * 60 * 1000;
    const newTimerId = setTimeout(() => {
        // 發送關閉命令 - 修正版
        const controlMsg = {
            payload: {
                domain: battery.domain,
                service: 'turn_off',
                target: {
                    entity_id: entityId
                }
            },
            batteryKey: batteryKey,
            action: 'auto_stop'
        };

        // 發送到控制輸出
        node.send([null, controlMsg, {
            payload: `自動關閉 ${battery.name}`,
            batteryKey: batteryKey,
            action: 'auto_stop',
            controlMsg: controlMsg
        }]);

        flow.set(`${batteryKey}_timer`, null);
    }, chargingMs);

    flow.set(`${batteryKey}_timer`, newTimerId);

    // 準備通知訊息
    const notifyMsg = {
        payload: {
            title: '🔋 充電開始',
            message: `🔋 ${battery.name} 開始充電\n⏱️ 預計時間：${battery.chargingTime}分鐘\n🕐 開始時間：${startTime.toLocaleString('zh-TW')}`
        },
        batteryKey: batteryKey,
        action: 'start'
    };

    outputs[0] = notifyMsg;
    outputs[2] = {
        payload: `${battery.name} 開始充電`,
        batteryKey: batteryKey,
        chargingTime: battery.chargingTime,
        timerId: newTimerId,
        entityId: entityId,
        domain: battery.domain
    };
}

// 檢查關閉狀態 (on -> off)
else if (newState === 'off' && oldState === 'on') {
    // 清除計時器
    const timerId = flow.get(`${batteryKey}_timer`);
    if (timerId) {
        clearTimeout(timerId);
        flow.set(`${batteryKey}_timer`, null);
    }

    // 計算實際時間
    const startTime = flow.get(`${batteryKey}_start`);
    const actualTime = startTime ? Math.round((Date.now() - startTime) / 60000) : 0;

    // 準備完成通知
    const notifyMsg = {
        payload: {
            title: '✅ 充電完成',
            message: `✅ ${battery.name} 充電完成\n⏱️ 充電時長：${actualTime}分鐘\n🕐 完成時間：${new Date().toLocaleString('zh-TW')}`
        },
        batteryKey: batteryKey,
        action: 'complete'
    };

    outputs[0] = notifyMsg;
    outputs[2] = {
        payload: `${battery.name} 充電完成`,
        batteryKey: batteryKey,
        actualTime: actualTime,
        entityId: entityId
    };
}

return outputs;

// 檢查是否有通知訊息
if (!msg || !msg.payload) {
    return [null, null, null];
}

// 檢查靜音時間 (22:00-07:00)
const now = new Date();
const hour = now.getHours();
const isQuiet = hour >= 22 || hour < 7;

if (isQuiet && msg.action !== 'error') {
    return [null, null, null];
}

const title = msg.payload.title;
const message = msg.payload.message;

// 創建三個通知訊息
const notify1 = {
    payload: {
        domain: 'notify',
        service: 'notify',
        data: {
            title: title,
            message: message
        }
    }
};

const notify2 = {
    payload: {
        domain: 'notify',
        service: 'synology_chat_bot_3',
        data: {
            title: title,
            message: message
        }
    }
};

const notify3 = {
    payload: {
        domain: 'notify',
        service: 'telegram',
        data: {
            title: title,
            message: message
        }
    }
};

return [notify1, notify2, notify3];
